</template>

<script>
import { Nav } from "/Data.json"

export default {
	data() {
		return {
			navLinks: null,
			listItem: null,
			menuIsOpen: false,
			navClasses: null,
			nav: Nav,
		}
	},
	mounted() {
		this.navLinks = document.querySelectorAll('.navLink')
		this.listItem = document.querySelectorAll('nav > ul > li')

		this.navLinks.forEach(element => {
			element.addEventListener('mouseleave', () => {
				element.classList.add('mouseLeave')
				setTimeout(() => {
					element.classList.remove('mouseLeave')
				}, 250)
			})
			element.addEventListener('blur', () => {
				element.classList.add('mouseLeave')
				setTimeout(() => {
					element.classList.remove('mouseLeave')
				}, 250)
			})
		})
	},
	methods: {
		openMenu() {
			if (!this.menuIsOpen) {
				;(this.menuIsOpen = true), (this.navClasses = 'open')
				this.listItem.forEach((link, index) => {
					link.style.animation = 'linksFade 0.5s ease forwards ' + (index / 5 + 0.25) + 's'
				})
			} else {
				this.listItem.forEach((link, index) => {
					link.style.animation = ''
				})
				this.menuIsOpen = false
				this.navClasses = 'close'
				setTimeout(() => {
					this.navClasses = null
				}, 250)
			}
		},
	},
}
</script>

